```typescript
import { Lumina } from "lumina-engine";
import "lumina-engine/style.css";

// Mount to a DOM element
const deck = new Lumina("#app", {
  loop: false,
  theme: {
    colors: {
      primary: "#10b981",
    },
  },
});

// Load Data
deck.load({
  meta: { title: "My Deck" },
  slides: [
    {
      type: "statement",
      title: "Hello World",
      subtitle: "Powered by Lumina",
    },
  ],
});
```

```typescript
deck.on("slideChange", (e) => {
  console.log(`Slide changed to ${e.index}`);
});

deck.on("action", (e) => {
  console.log("User interaction:", e);
});
```
